@prefix ex: <http://example.neo4j.com/graphvalidation#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix neo4j: <neo4j://graph.schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

ex:CustShape a sh:NodeShape ;
  sh:targetClass neo4j:Customer ;
  sh:property [
    sh:path neo4j:companyName ;
    sh:pattern "^\\w[\\s\\w\\.]*$" ;
    sh:maxCount 1 ;
    sh:datatype xsd:string ;
  ];
  sh:property [
    sh:path neo4j:PURCHASED ;
    sh:class neo4j:Order ;
    sh:nodeKind sh:IRI ;
    sh:minCount 10 ;
  ] .


ex:ProdShape a sh:NodeShape ;
  sh:targetClass neo4j:Product ;
  sh:property [
    sh:path neo4j:productName ;
    sh:pattern "^\\w[\\s\\w\\.]*$" ;
    sh:maxCount 1 ;
    sh:datatype xsd:string ;
  ];
  sh:property [
    sh:path neo4j:unitPrice ;
    sh:maxInclusive 20 ;
    sh:maxCount 1 ;
  ];
  sh:property [
    sh:path [ sh:inversePath neo4j:ORDERS ] ;
    sh:class neo4j:Order ;
    sh:minCount 1 ;
    sh:nodeKind sh:IRI ;
  ] ;
.
